mapper.select:
SELECT * 
FROM UserFeeList
WHERE AccountId = #{accountId} and Date like #{date}+'%' and Part=#{part}

mapper.update:
UPDATE UserFeeList
SET
    date=#{date}
    ,content=#{content}
    ,place=#{place}
    ,companion=#{companion}
    ,method=#{method}
    ,accountId=#{accountId}
    ,part=#{part}
    ,state=#{state}
    ,price=#{price}
where id=#{id}

UPDATE UserFeeList
SET state=#{state}
WHERE id=#{id}
